"use client";

import React, { forwardRef, useRef } from "react";

import { cn } from "@/lib/utils";
import Image from "next/image";
import { AnimatedBeam } from "../magicui/animated-beam";

// Circular node component that represents each surrounding entity
const CircleNode = forwardRef<
  HTMLDivElement,
  { className?: string; children?: React.ReactNode }
>(({ className, children }, ref) => {
  return (
    <div
      ref={ref}
      className={cn(
        "border-foundation-dark-muted-blue-gray bg-foundation-dark-muted-blue z-10 flex size-12 items-center justify-center rounded-full border",
        className,
      )}
    >
      {children}
    </div>
  );
});

CircleNode.displayName = "CircleNode";

// Central node component that represents OptiGrid
const CenterNode = forwardRef<
  HTMLDivElement,
  { className?: string; children?: React.ReactNode }
>(({ className, children }, ref) => {
  return (
    <div
      ref={ref}
      className={cn("z-10 flex items-center justify-center", className)}
    >
      <div className="rounded-full bg-[#162435] p-2.5">
        <div className="rounded-full bg-[#1C2D40] p-2.5">
          <div className="bg-foundation-light-normal rounded-full p-2.5">
            <div className="flex h-full w-full items-center justify-center">
              {children}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
});

CenterNode.displayName = "CenterNode";

export default function AnimatedBeamMultipleInputs({
  className,
}: {
  className?: string;
}) {
  // Create refs for all nodes
  const containerRef = useRef<HTMLDivElement>(null);
  const centerRef = useRef<HTMLDivElement>(null);

  // Refs for surrounding nodes (clockwise from top)
  const aemoRef = useRef<HTMLDivElement>(null);
  const transmissionRef = useRef<HTMLDivElement>(null);
  const windRef = useRef<HTMLDivElement>(null);
  const solarRef = useRef<HTMLDivElement>(null);
  const meterRef = useRef<HTMLDivElement>(null);
  const batteryRef = useRef<HTMLDivElement>(null);

  return (
    <div
      className={cn(
        "relative flex h-[186px] w-full items-center justify-center overflow-hidden",
        className,
      )}
      ref={containerRef}
    >
      {/* Layout nodes in a circular pattern */}
      <div className="relative h-44 w-full md:w-1/2">
        {/* Center OptiGrid node */}
        <CenterNode
          ref={centerRef}
          className="absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2"
        >
          <div className="flex h-full w-full items-center justify-center">
            <Icons.optigrid />
          </div>
        </CenterNode>

        {/* AEMO Node - Top left */}
        <CircleNode
          ref={aemoRef}
          className="absolute top-[12%] left-[3%] -translate-y-1/2"
        >
          <Icons.aemo />
        </CircleNode>

        {/* Meter Box Node - Bottom Left */}
        <CircleNode
          ref={meterRef}
          className="absolute bottom-[11%] left-[3%] translate-y-1/2"
        >
          <Icons.meter />
        </CircleNode>

        {/* Battery Node - Middle Left */}
        <CircleNode
          ref={batteryRef}
          className="-1left-[10%] absolute top-2/4 -translate-y-1/2"
        >
          <Icons.battery />
        </CircleNode>

        {/* Transmission Tower Node - Top Right */}
        <CircleNode
          ref={transmissionRef}
          className="absolute -top-[2%] right-0 -translate-x-1/2"
        >
          <Icons.transmission />
        </CircleNode>

        {/* Wind Turbine Node - Middle Right */}
        <CircleNode
          ref={windRef}
          className="absolute right-[9%] bottom-[36%] translate-x-1/2 md:right-[5%]"
        >
          <Icons.wind />
        </CircleNode>

        {/* Solar Panel Node - Bottom */}
        <CircleNode
          ref={solarRef}
          className="absolute right-0 -bottom-[2%] -translate-x-1/2"
        >
          <Icons.solar />
        </CircleNode>
      </div>

      {/* Animated beams connecting all nodes to the center */}
      <AnimatedBeam
        containerRef={containerRef}
        fromRef={aemoRef}
        toRef={centerRef}
        pathColor="#4c6885"
        pathOpacity={0.5}
        pathWidth={1.5}
        curvature={40}
        gradientStartColor="#4c6885"
        gradientStopColor="#375a81"
      />
      <AnimatedBeam
        containerRef={containerRef}
        fromRef={transmissionRef}
        toRef={centerRef}
        pathColor="#4c6885"
        pathOpacity={0.5}
        pathWidth={1.5}
        curvature={40}
        gradientStartColor="#4c6885"
        gradientStopColor="#375a81"
      />
      <AnimatedBeam
        containerRef={containerRef}
        fromRef={windRef}
        toRef={centerRef}
        pathColor="#4c6885"
        pathOpacity={0.5}
        pathWidth={1.5}
        curvature={0}
        gradientStartColor="#4c6885"
        gradientStopColor="#375a81"
      />
      <AnimatedBeam
        containerRef={containerRef}
        fromRef={solarRef}
        toRef={centerRef}
        pathColor="#4c6885"
        pathOpacity={0.5}
        pathWidth={1.5}
        curvature={-40}
        gradientStartColor="#4c6885"
        gradientStopColor="#375a81"
      />
      <AnimatedBeam
        containerRef={containerRef}
        fromRef={meterRef}
        toRef={centerRef}
        pathColor="#4c6885"
        pathOpacity={0.5}
        pathWidth={1.5}
        curvature={-40}
        gradientStartColor="#4c6885"
        gradientStopColor="#375a81"
      />
      <AnimatedBeam
        containerRef={containerRef}
        fromRef={batteryRef}
        toRef={centerRef}
        pathColor="#4c6885"
        pathOpacity={0.5}
        pathWidth={1.5}
        curvature={0}
        gradientStartColor="#4c6885"
        gradientStopColor="#375a81"
      />
    </div>
  );
}

